  setChartData() {
    this.barchartData = [];
    const barChartData = [];
    this.generateColors();
    Object.values(this.chartblfldata).forEach((each, index) => {
      if (each.occupiedarea > 0) {
        let dataobj = {
          name: each.name,
          value: each.occupiedarea
        };
        barChartData.push(dataobj);
      }
    });
    if (barChartData.length < 5) {
      let empty = '  ';
      for (let i = 0; i < 5 - barChartData.length; i++) {
        barChartData.push({
          name: empty.repeat(i + 1),
          value: 0
        });
      }
    }
    this.barchartData = barChartData;
    this.legendItems = this.barchartData.filter(item => item.value > 0);
    this.cdr.detectChanges();
  }
  generateColors() {
    const dynamicColors = [];
    Object.values(this.chartblfldata).forEach((each, index) => {
      if (each.occupiedarea > 0) {
        let color = '';
        do {
          color = `rgb(${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)})`;
        } while (color === 'rgb(255, 255, 255)' || this.isWhiteColorShade(color) || dynamicColors.includes(color));
        {
          color = `rgb(${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)})`;
        }
        ;
        dynamicColors.push(color);
      }
    });
    this.customColor = {
      domain: [...dynamicColors]
    };
  }
  isWhiteColorShade(color) {
    const rgbValues = color.match(/\d+/g);
    const r = parseInt(rgbValues[0]);
    const g = parseInt(rgbValues[1]);
    const b = parseInt(rgbValues[2]);
    const threshold = 200;
    const average = (r + g + b) / 3;
    return average > threshold;
  }
