      blId: blId,
      flId: flId,
      divId: divId,
      depId: depId,
      viewBy: viewBy,
      subDepId: subDepId
    }

    if (displayType == 'grid') {
      this.loadGridBlFlData();
    } else {
      this.blServ.getspaceallocationstatictics(data).subscribe((res: any) => {
        if (res) {
          this.chartblfldata = res;
