  onSelection(event, name) {
    this.loadTabData(name);
    event.preventDefault();
  }
  loadTabData(name) {
    this.hidePrevTab(this.tab_name_clicked);
    this.tab_name_clicked = name;
    $("#locTabContent").hide();
    switch (this.tab_name_clicked) {
      case "daily":
        {
          $("#locTabContent,#daily").show();
          this.isError = false;
          break;
        }
      case "weekly":
        {
          $("#locTabContent,#weekly").show();
          this.isError = false;
          break;
        }
      case "monthly":
        {
          $("#locTabContent,#monthly").show();
          this.isError = false;
          break;
        }
      case "annually":
        {
          $("#locTabContent,#annually").show();
          var year = new Date().getFullYear() + 10;
          this.maxDate = new Date(new Date().setFullYear(year));
          this.isError = false;
          break;
        }
      default:
        {
          break;
        }
    }
  }
  hidePrevTab(name) {
    switch (name) {
      case "daily":
        {
          $("#daily").hide();
          break;
        }
      case "weekly":
        {
          $("#weekly").hide();
          break;
        }
      case "monthly":
        {
          $("#monthly").hide();
          break;
        }
      case "annually":
        {
          $("#annually").hide();
          break;
        }
      default:
        {
          break;
        }
    }
  }
  onSelectMonthlyType(event) {
