import { Component, OnInit } from '@angular/core';
import { MatDialogConfig } from '@angular/material/dialog';
import { UtilConstant } from 'src/common/UtilConstant';
import { ArrangementDialogueProvider } from '../provider/arrangement.provider';
import { ArrangementService } from '../services/arrangement.service';
import { PaginationObj } from 'src/app/model/pagination-model';

@Component({
  selector: 'app-arrangement',
  templateUrl: './arrangement.component.html',
  styleUrls: ['./arrangement.component.scss']
})
export class ArrangementComponent implements OnInit {

  arrangementRecords: [] = [];
  loadingRec: boolean = false;
  rowCount: number = UtilConstant.ROW_COUNT;
  totalElements:number = 0;
  paginationObj:PaginationObj = {
    pageNo:0,
    pageSize:this.rowCount,
    sortBy:["arrangementId"],
    sortOrder:"ASC"
  }
  filterCriteria:any = {};
  isFiltered:boolean = false;
  filterCriteriaList :any[]=[];
  constructor(
    private arrangeSrv: ArrangementService,
    private arrangementProvider: ArrangementDialogueProvider,
  ) { }

  ngOnInit(): void {
    this.loadRecords();
  }

  loadRecords() {
    this.loadingRec = true;
    let data={paginationDTO:this.paginationObj,filterCriteria:this.filterCriteriaList};
    this.arrangeSrv.getAllArrangementsPaginated(data).subscribe((res: any) => {
      if (res) {
        this.loadingRec = false;
        this.isFiltered= false;
        this.arrangementRecords = res.content ? res.content : [];
        this.totalElements = res.totalElements ? res.totalElements : 0;
      }
      else {
        this.loadingRec = true;
        this.arrangementRecords = [];
      }
    },
      error => {
        this.loadingRec = false;
      }
    );
  }

  addRecord() {
    const dialogConfig = new MatDialogConfig();
    dialogConfig.disableClose = true;
    dialogConfig.autoFocus = true;
    dialogConfig.width = '750px';
    dialogConfig.data = {
      arrangementId:0,
      arrangementType: "",
      description: "",
      isEdit: true,
      newRecord: true,
      title: "Add"
    };
    this.arrangementProvider.openDialog(dialogConfig);
    this.arrangementProvider.onDialogueClosed.subscribe((result: any) => {
      if (result == true) {
        this.loadRecords();
      }
    });
  }

  openEditItem(arrange: any) {
    const dialogConfig = new MatDialogConfig();
    dialogConfig.disableClose = true;
    dialogConfig.autoFocus = true;
    dialogConfig.width = '750px';
    dialogConfig.data = {
      arrangementId:arrange.arrangementId,
      arrangementType: arrange.arrangementType,
      description: arrange.description,
      isEdit: true,
      newRecord: false,
      title: "Edit"
    };
    this.arrangementProvider.openDialog(dialogConfig);
    this.arrangementProvider.onDialogueClosed.subscribe((result: any) => {
      if (result == true) {
        this.loadRecords();
      }
    })
  }

  onPageChange(event:any){
    const pageNo = event.first ? event.first / event.rows : 0;
    const pageSize = event.rows;
    this.paginationObj.pageNo = pageNo;
    this.paginationObj.pageSize = pageSize;
    this.loadRecords();
  }

  onInnerFilter(event: any) {
    if (this.isFiltered) {
      Object.keys(event.filters).forEach((field) => {
        const filterValue = event.filters[field][0].value;
        const matchMode = event.filters[field][0].matchMode;
        if(filterValue !== undefined){
          let filterCriteria = { fieldName: field, value: filterValue, matchMode: matchMode };
          this.updateFilterCriteriaList(filterCriteria);
        }
      });
      this.paginationObj.pageNo = 0;
      this.loadRecords();
    }
    this.isFiltered = true;
  }

  updateFilterCriteriaList(filterCriteria:any){
    let index = this.filterCriteriaList.findIndex(item => item.fieldName === filterCriteria['fieldName']);
    if(filterCriteria['value']==null){
      if(index !==-1){
        this.filterCriteriaList.splice(index, 1);
      }
    }else {
      if (index !== -1) {
        this.filterCriteriaList[index] = filterCriteria;
      } else {
        this.filterCriteriaList.push(filterCriteria);
      }
    }
  }

}
<div class="card" style="height:92vh;">
        <p-panel header="Arrangements">
                <ng-template pTemplate="icons">
                        <button pButton (click)="addRecord()" label="Add" icon="pi pi-plus"></button>
                </ng-template>
                <div class="card">
                        <p-table  [value]="arrangementRecords" [paginator]="false" [loading]="loadingRec" 
                                 [scrollable]="true" scrollDirection="both" scrollHeight="500px" (onFilter)="onInnerFilter($event)"
                                [rowHover]="false"   styleClass="p-datatable-customers p-datatable-gridlines" dataKey="arrangementId">
                                <ng-template pTemplate="header">
                                        <tr>     
                                                <th style="width:4rem"></th>
                                                <th pSortableColumn="arrangementType">
                                                    <div class="grid-header-column">
                                                            <div class="grid-header-label">
                                                              <p class="grid-label-paragraph">Arrangement Type</p>
                                                            </div>
                                                            <div class="grid-sort-icon">
                                                            <p-sortIcon field="arrangementType"></p-sortIcon>
                                                            <p-columnFilter type="text" field="arrangementType" display="menu"></p-columnFilter>
                                                              </div>
                                                           </div>
                                                </th>
                                                <th pSortableColumn="description">
                                                    <div class="grid-header-column">
                                                            <div class="grid-header-label">
                                                              <p class="grid-label-paragraph">Description</p>
                                                            </div>
                                                            <div class="grid-sort-icon">
                                                            <p-sortIcon field="description"></p-sortIcon>
                                                            <p-columnFilter type="text" field="description" display="menu"></p-columnFilter>
                                                              </div>
                                                           </div>
                                                </th>  
                                                <th>Highlight Color</th>
                                        </tr>
                                </ng-template>
                                <ng-template pTemplate="body" let-arrange>
                                        <tr [pSelectableRow]="arrange">   
                                                <td style="width:4rem">
                                                        <button icon="pi pi-pencil" pButton pRipple type="button"
                                                                (click)="openEditItem(arrange)" class="p-button-text p-button-rounded"></button>

                                                </td>
                                                <td>{{arrange.arrangementType}}</td>
                                                <td>{{arrange.description}}</td>
                                                <td>
                                                    <div [style.background-color]="arrange.highlightColor" class="td-class">
                                                    </div>
                                                </td>
                                        </tr>
                                </ng-template>
                                <ng-template pTemplate="emptymessage" let-columns>
                                        <tr>
                                                <td [attr.colspan]="4">
                                                        No records found
                                                </td>
                                        </tr>
                                </ng-template>
                        </p-table>
                        <div class="paginator">
                                <p-paginator  [rows]="rowCount" [totalRecords]="totalElements" (onPageChange)="onPageChange($event)"
                                [first]="paginationObj.pageNo * paginationObj.pageSize"
                                  currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries" [showCurrentPageReport]="true"
                                  [rowsPerPageOptions]="[25,50,100]"></p-paginator>
                        </div>
                </div>
            </p-panel>
</div>
