import { Component } from '@angular/core';
import { ConfirmationService, MessageService } from 'primeng/api';
import { UtilConstant } from 'src/common/UtilConstant';
import { TermsService } from './services/terms.service';
import { MatDialogConfig } from '@angular/material/dialog';
import { TermsDialogueProvider } from './providers/terms.provider';
import { PaginationObj } from 'src/app/model/pagination-model';

@Component({
  selector: 'app-terms',
  templateUrl: './terms.component.html',
  styleUrls: ['./terms.component.scss'],
  providers: [MessageService]
})
export class TermsComponent {

  termsRecords: [] = [];
  loadingRec: boolean = false;
  rowCount: number = UtilConstant.ROW_COUNT;
  paginationObj:PaginationObj = {
    pageNo:0,
    pageSize:this.rowCount,
    sortBy:["termId"],
    sortOrder:"ASC"
  }
  totalElements:number = 0;
  isFiltered:boolean = false;
  filterCriteriaList :any[]=[];
  isSorted : boolean = false;
  constructor(
    private termsservice:TermsService,
    private termsDialogue: TermsDialogueProvider,
    private messageService: MessageService,
    private confirmationService: ConfirmationService,
  ) { }

  ngOnInit(): void {
    this.loadRecords();
  }

  loadRecords(){
    this.loadingRec= true;
    this.isFiltered = false;
    let data = {paginationDTO:this.paginationObj,filterCriteria:this.filterCriteriaList};
    this.termsservice.getAllTermsPaginated(data).subscribe((res:any)=>{
      if(res.status!=202){
        this.loadingRec= false;
        this.isFiltered = false;
        this.termsRecords =  res.content ? res.content : [];
        this.totalElements = res.totalElements ? res.totalElements : 0;
      }else{
        this.loadingRec= true;
        this.termsRecords = [];
      }
    })
  }

  addRecord(){
    const dialogConfig = new MatDialogConfig();
    dialogConfig.disableClose = true;
    dialogConfig.autoFocus = true;
    dialogConfig.width = '550px';
    dialogConfig.data = {
      termId:0,
      term: "",
      dateFrom: null,
      dateTo: null,
      isEdit: true,
      newRecord: true,
      title: "Add",
      comments:null
    };
    this.termsDialogue.openDialog(dialogConfig);
    this.termsDialogue.onDialogueClosed.subscribe((result: any) => {
      if (result == true) {
        this.loadRecords();
      }
    });
  }

  openEditItem(term:any){
    const dialogConfig = new MatDialogConfig();
    dialogConfig.disableClose = true;
    dialogConfig.autoFocus = true;
    dialogConfig.width = '550px';
    dialogConfig.data = {
      termId:term.termId,
      term: term.term,
      dateFrom: term.dateFrom,
      dateTo: term.dateTo,
      isEdit: true,
      newRecord: false,
      title: "Edit",
      comments:term.comments
    };
    this.termsDialogue.openDialog(dialogConfig);
    this.termsDialogue.onDialogueClosed.subscribe((result: any) => {
      if (result == true) {
        this.loadRecords();
      }
    })
  }

  onDelete(term: any) {
    this.confirmationService.confirm({
      message: 'Are you sure that you want to delete '+term.term+'?',
      header: 'Confirmation',
      icon: 'pi pi-exclamation-triangle',
      accept: () => {
        this.deleteRecord(term);
      },
      key: "termDetails"
    });
  }

  deleteRecord(term:any){
    this.messageService.clear();
    this.termsservice.deleteTerm(term).subscribe((res:any)=>{
      if(res.code == 200){
        this.messageService.add({ key: 'termMessage', severity: 'success', summary: 'Record deleted successfully', detail: 'Record deleted successfully' });
        setTimeout(()=>{
          this.loadRecords();
        },1000)
      }
      else{
        this.messageService.add({ key: 'termMessage', severity: 'warn', summary: 'Delete failed', detail: res.text });
        setTimeout(()=>{
          this.loadRecords();
        },1000)
      }
    })
  }

  onPageChange(event: any) {
    const pageNo = event.first ? event.first / event.rows : 0;
    const pageSize = event.rows;
    this.paginationObj.pageNo = pageNo;
    this.paginationObj.pageSize = pageSize;
    this.loadRecords();
  }

  onSort(event: any) {
   // this.isSorted = true;
  }

  onInnerFilter(event: any) {
    this.isSorted = false;
    if(this.isFiltered && !this.isSorted){
      this.isSorted = false;
      Object.keys(event.filters).forEach((field) => {
        const filterValue = event.filters[field][0].value;
        const matchMode = event.filters[field][0].matchMode;
        if (filterValue !== undefined ) {
          let filterCriteria = { fieldName: field, value: filterValue, matchMode: matchMode };
          this.updateFilterCriteriaList(filterCriteria);
        }
      });
      // if(this.filterCriteriaList.length!=0){
      //   this.paginationObj.pageNo = 0;
      // }
      this.paginationObj.pageNo = 0;
      this.loadRecords();
    }
    this.isFiltered = true;
  }

  updateFilterCriteriaList(filterCriteria:any){
    let index = this.filterCriteriaList.findIndex(item => item.fieldName === filterCriteria['fieldName']);
    if(filterCriteria['value']==null){
      if(index !==-1){
        this.filterCriteriaList.splice(index, 1);
      }
    }else {
      if (index !== -1) {
        this.filterCriteriaList[index] = filterCriteria;
      } else {
        this.filterCriteriaList.push(filterCriteria);
      }
    }
  }

}
<div class="card" style="height:92vh;">
    <p-panel header="Manage Terms">
            <ng-template pTemplate="icons">
                    <button pButton (click)="addRecord()" label="Add" icon="pi pi-plus"></button>
            </ng-template>
            <div class="card">
                <p-table [value]="termsRecords" [paginator]="false" [rows]="rowCount" [loading]="loadingRec" [scrollable]="true"
                        scrollDirection="both" scrollHeight="550px" [rowHover]="false"
                        styleClass="p-datatable-customers p-datatable-gridlines" dataKey="termId" (onFilter)="onInnerFilter($event)"
                        (onSort)="onSort($event)">
                            <ng-template pTemplate="header">
                                    <tr>     
                                            <th style="width:8rem"></th>
                                            <th pSortableColumn="term">
                                                <div class="grid-header-column">
                                                        <div class="grid-header-label">
                                                          <p class="grid-label-paragraph">Term</p>
                                                        </div>
                                                        <div class="grid-sort-icon">
                                                        <p-sortIcon field="term"></p-sortIcon>
                                                        <p-columnFilter type="text" field="term" display="menu"></p-columnFilter>
                                                          </div>
                                                </div>
                                            </th>
                                            <th pSortableColumn="dateFrom">
                                                <div class="grid-header-column">
                                                        <div class="grid-header-label">
                                                          <p class="grid-label-paragraph">Date From</p>
                                                        </div>
                                                        <div class="grid-sort-icon">
                                                                <p-sortIcon field="dateFrom"></p-sortIcon>
                                                                <p-columnFilter type="date" field="dateFrom" display="menu" >
                                                                </p-columnFilter>
                                                        </div>
                                                </div>
                                            </th>
                                            <th pSortableColumn="dateTo">
                                                <div class="grid-header-column">
                                                        <div class="grid-header-label">
                                                          <p class="grid-label-paragraph">Date To</p>
                                                        </div>
                                                        <div class="grid-sort-icon">
                                                        <p-sortIcon field="dateTo"></p-sortIcon>
                                                        <p-columnFilter type="date" field="dateTo" display="menu"></p-columnFilter>
                                                          </div>
                                                </div>
                                             </th>
                                             <th>
                                                Notes
                                            </th>
                                    </tr>
                            </ng-template>
                            <ng-template pTemplate="body" let-term>
                                    <tr [pSelectableRow]="term">   
                                            <td style="width:8rem">
                                                    <button icon="pi pi-pencil" pButton pRipple type="button"
                                                            (click)="openEditItem(term)" class="p-button-text p-button-rounded"></button>
                                                    <button pButton type="button" class="p-button-text p-button-rounded pull-right" style="font-size: 5rem; color: red;" icon="pi pi-times"
                                                            iconPos="left" (click)="onDelete(term)"></button>
                                            </td>
                                            <td>{{term.term}}</td>
                                            <td>{{term.dateFrom | date:'dd MMM yyyy'}}</td>
                                            <td>{{term.dateTo | date:'dd MMM yyyy'}}</td>  
                                            <td matTooltip="{{term.comments}}" matTooltipPosition="below" class="ellipsis">{{term.comments}}</td> 
                                    </tr>
                            </ng-template>
                            <ng-template pTemplate="emptymessage" let-columns>
                                    <tr>
                                            <td [attr.colspan]="4">
                                                    No records found
                                            </td>
                                    </tr>
                            </ng-template>
                    </p-table>
                    <div class="paginator">
                        <p-paginator [rows]="rowCount" [totalRecords]="totalElements" (onPageChange)="onPageChange($event)"
                                [first]="paginationObj.pageNo * paginationObj.pageSize"
                                currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries" [showCurrentPageReport]="true"
                                [rowsPerPageOptions]="[25,50,100]"></p-paginator>
                      </div>
            </div>
        </p-panel>
</div>
<p-toast key="termMessage" position="top-right"></p-toast>
