  <table width="100%">
    <tr>
      <td style="width: 100%">
        <div class="input-label-custom ">
          <label class='form-field-lable control-label'>Room Category</label>
          <ng-select appendTo="body" [clearable]="false" [items]="enumRmcats" formControlName="rmCat"
            [readonly]="!isNew" [searchable]="true" bindLabel="rmCatDesc" bindValue="rmCat"
            placeholder="Make a selection" class="ng-select-dropdown">
          </ng-select>
        </div>
      </td>
    <tr>
      <td style="width: 100%">
        <div class="input-label-custom ">
          <label class='form-field-lable control-label'>Room Type</label>
          <input type="text" class="form-control" name="rmType" (keydown.space)="$event.preventDefault();" formControlName="rmType" maxlength="32"
            [readonly]="!isNew">
        </div>
      </td>
    </tr>
    <tr *ngIf="(rmTypeFormPanel.controls['rmType']?.errors && rmTypeFormPanel.controls['rmType'].dirty)">      
      <td style="width:calc(100% / 2)">
        <label *ngIf="rmTypeFormPanel.controls['rmType']?.errors &&  !rmTypeFormPanel.controls['rmType']?.errors.required" class='form-field-lable' style="color:red;">Room Type is already exists</label>
      </td>

      <td style="width:calc(100% / 2)">
      </td>
    </tr>
    <tr>
      <td style="width: 100%" >
        <div class="input-label-custom ">
          <label class='form-field-lable'>Description</label>
          <nz-textarea-count [nzMaxCharacterCount]="64">
            <textarea class="form-control note-border" name="rmTypeDesc" formControlName="rmTypeDesc" rows="2" nz-input maxlength="64"></textarea>
          </nz-textarea-count>
        </div>
      </td>
    </tr>
    <tr>
      <td style="width: 100%">
              <div class="input-label-custom">
                      <label class='form-field-lable control-label'>Highlight Color </label>
                      <input type="color" class="form-control" name="highlightColor" style="width: 100px;padding: 0.375rem;"
                              formControlName="highlightColor" maxlength="32" >
              </div>
      </td>
    </tr>

  </table>
</form>
