.rm-details-para2[_ngcontent-%COMP%] {\n  font-size: 12px;\n  font-weight: 500;\n  text-align: left;\n  margin-left: 5px;\n  padding-top: 0;\n}\n\n.border-bottom[_ngcontent-%COMP%] {\n  border-bottom: 1px solid rgb(232, 226, 226);\n  font-size: 14px;\n}\n\n.row[_ngcontent-%COMP%] {\n  margin-left: 1px;\n  margin-right: 1px;\n  background-color: #fff;\n  background-clip: border-box;\n  border-radius: 0.25rem;\n}\n\n.column1[_ngcontent-%COMP%] {\n  float: left;\n  width: 50%;\n  padding: 10px;\n}\n\n.column2[_ngcontent-%COMP%] {\n  float: left;\n  width: 50%;\n  padding: 10px;\n}\n\n.sum-para[_ngcontent-%COMP%] {\n  font-size: 12px !important;\n  padding: 0 !important;\n  font-weight: 500 !important;\n  padding-left: 3px !important;\n  margin-bottom: 1px;\n}\n\n.sum-label[_ngcontent-%COMP%] {\n  font-size: 11px !important;\n  padding: 0 !important;\n  font-weight: normal !important;\n  padding-left: 3px !important;\n  margin-top: -5px;\n  margin-left: 16px;\n  font-family: \"Roboto\";\n}\n\n.avail-rooms[_ngcontent-%COMP%] {\n  margin-left: 28px;\n  margin-top: 5px;\n  margin-bottom: 0px;\n  font: 400 15px/20px Roboto, \"Helvetica Neue\", sans-serif;\n}\n\n.room-card[_ngcontent-%COMP%] {\n  min-height: 63vh;\n}\n\n.detailsHead[_ngcontent-%COMP%] {\n  font-size: 14px;\n  margin-bottom: 0px;\n  font-weight: 500;\n}\n\n.detailsPara[_ngcontent-%COMP%] {\n  font-size: 13px !important;\n  padding: 0 !important;\n  font-weight: normal !important;\n  padding-left: 3px !important;\n  margin-top: -5px;\n  margin-left: 16px;\n  font-family: revert;\n}\n\n.no-room-card[_ngcontent-%COMP%] {\n  min-height: 63vh;\n  display: flex;\n  justify-content: center;\n  align-items: center;\n}\n\n.no-avail-rooms[_ngcontent-%COMP%] {\n  font: 150 25px/30px Roboto, \"Helvetica Neue\", sans-serif;\n  letter-spacing: normal;\n  opacity: 0.4;\n}\n\n.recurrence-label[_ngcontent-%COMP%] {\n  font-size: 12px;\n}\n\n.booking-card[_ngcontent-%COMP%] {\n  display: flex;\n  flex-direction: column;\n  jus