          } else if (field == "requestedByUserUserName") {
            filterCriteria = {
              fieldName: "U.user_name",
              value: filterValue,
              matchMode: matchMode
            };
          } else if (field == "bookingType") {
            filterCriteria = {
              fieldName: "type",
              value: filterValue,
              matchMode: matchMode
            };
          } else {
            filterCriteria = {
              fieldName: field,
              value: filterValue,
              matchMode: matchMode
            };
          }
