import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { UserSreenProcs } from '../../model/dto/user-screen-procs.dto';
import { UserProcsService } from '../../service/user-procs.service';

@Component({
  selector: 'app-user-unassign-screens',
  templateUrl: './user-unassign-screens.component.html',
  styleUrls: ['./user-unassign-screens.component.scss']
})
export class UserUnassignScreensComponent implements OnInit {

  userUnAssignScreens:any[]=[];
  role: string = "";
  selectedScreens:any[]=[];
  userRoleId:number = 0;
  @Output() allScreenProcsPanels = new EventEmitter();
  constructor(
    private userProcsSrv: UserProcsService,
  ) { }

  ngOnInit(): void {
  //  this.loadUserUnAssignScreens(this.role);

  }
  loadUserUnAssignScreens(userRoleData:any):void{
    this.role = userRoleData.roleName;
    this.userRoleId = userRoleData.userRoleId;
    this.selectedScreens = [];
    if (this.role.length >1){
      let postData = {
        "userRoleId": this.userRoleId
      };
      this.userProcsSrv.getUnAssignUserScreens(postData).subscribe((res) => {
        if(this.role === 'Application Admin'){
          this.userUnAssignScreens = res;
        }else{
          this.userUnAssignScreens = res.filter(t => t.screenName != "Add or Edit Company");
          this.userUnAssignScreens = this.userUnAssignScreens.filter(t =>  t.screenName  != "User Plan Report");
          this.userUnAssignScreens = this.userUnAssignScreens.filter(t =>  t.screenName != "Add or Edit Cost" );
        }
      }, error => {

      })
    }
    else
      this.userUnAssignScreens = [];

  }
  onAssign(){
    const dataRecord: UserSreenProcs = {
      userProcs: this.prepareDataRecord()
    }
    this.userProcsSrv.saveUserAssignScreens(dataRecord).subscribe((res) => {
      this.allScreenProcsPanels.emit("true");
    }, error => { console.log(error); });
  }
  prepareDataRecord(){
    let dataArray:any[]=[];

    this.selectedScreens.forEach(x=>{
      const rec:any ={
        "processId": x.processId,
        "screenNum":x.screenNum,
        "userScreensNum":0,
        "userRoleId":this.userRoleId,
        "subProcessId":x.subProcessId
      }
      dataArray.push(rec);
    });
    return dataArray;
  }

}
<div class="card">
  <p-table [value]="userUnAssignScreens" [(selection)]="selectedScreens" dataKey="screenNum"
   [globalFilterFields]="['','processId','subProcessId','screenName']"
  [scrollable]="true" scrollHeight="200px">
    <ng-template pTemplate="caption">
      <div class="d-flex flex-row">
        <div style="flex: auto;">
          <h4>Assign Processes</h4>
        </div>

        <div>
          <button pButton pRipple label="Assign" icon="pi pi-plus" class="p-button-primary p-button-sm p-mr-2"
            (click)="onAssign()" [disabled]="!(selectedScreens.length>0)"></button>

        </div>
      </div>
    </ng-template>
    <ng-template pTemplate="header">
      <tr>
        <th style="width: 3rem">
          <p-tableHeaderCheckbox></p-tableHeaderCheckbox>
        </th>
        <th pSortableColumn="process.processCode">
          <div class="grid-header-column">
            <div class="grid-header-label">
              <p class="grid-label-paragraph">Processes</p>
            </div>
            <div class="grid-sort-icon">
            <p-sortIcon field="process.processCode"></p-sortIcon>
            <p-columnFilter type="text" field="process.processCode" display="menu"></p-columnFilter>
              </div>
           </div>
        </th>
        <th pSortableColumn="subProcess.subProcessCode">
          <div class="grid-header-column">
            <div class="grid-header-label">
              <p class="grid-label-paragraph">Subprocesses</p>
            </div>
            <div class="grid-sort-icon">
            <p-sortIcon field="subProcess.subProcessCode"></p-sortIcon>
            <p-columnFilter type="text" field="subProcess.subProcessCode" display="menu"></p-columnFilter>
              </div>
           </div>
        </th>
        <th pSortableColumn="screenName">
          <div class="grid-header-column">
            <div class="grid-header-label">
              <p class="grid-label-paragraph">Screen Name</p>
            </div>
            <div class="grid-sort-icon">
            <p-sortIcon field="screenName"></p-sortIcon>
            <p-columnFilter type="text" field="screenName" display="menu"></p-columnFilter>
              </div>
           </div>
        </th>

      </tr>
    </ng-template>
    <ng-template pTemplate="body" let-unassign>
      <tr>
        <td style="width: 3rem">
          <p-tableCheckbox [value]="unassign"></p-tableCheckbox>
        </td>
        <td>{{unassign.process.processCode}}</td>
        <td>{{unassign.subProcess ? unassign.subProcess.subProcessCode : ''}}</td>
        <td>{{unassign.screenName}}</td>

      </tr>
    </ng-template>
    <ng-template pTemplate="emptymessage" let-columns>
      <tr>
        <td [attr.colspan]="4">
          No records found
        </td>
      </tr>
    </ng-template>
  </p-table>
</div>
