import { Component, OnInit } from '@angular/core';
import { BudgetTerm } from './model/BudgetTerm';
import { InventoryService } from '../service/inventory.service';
import { ConfirmationService, MessageService } from 'primeng/api';
import { UtilConstant } from 'src/common/UtilConstant';

@Component({
  selector: 'app-budget-terms-list',
  templateUrl: './budget-terms-list.component.html',
  styleUrls: ['./budget-terms-list.component.scss'],
  providers: [MessageService],
})
export class BudgetTermsListComponent implements OnInit {
  budegetTermList: BudgetTerm[] = [];
  budegetTermData: any;
  rowCount: number = UtilConstant.ROW_COUNT;
  isOpen: boolean = false;
  constructor(
    private inventorySrv: InventoryService,
    private confirmationService: ConfirmationService,
    private messageService: MessageService
  ) {}
  ngOnInit(): void {
    this.loadBudegetTermList();
  }

  loadBudegetTermList() {
    this.inventorySrv.getAllBudegetTerm().subscribe((res: any) => {
      if (res && !res.code) {
        this.budegetTermList = res;
      }
    });
  }

  openEditItem(id: number) {
    this.budegetTermData = {
      budgetTermId: id,
      isNew: false,
    };
    this.isOpen = true;
  }

  onAdd() {
    this.budegetTermData = {
      budgetTermId: 0,
      isNew: true,
    };
    this.isOpen = true;
  }
  onDelete(data: BudgetTerm) {
    this.userDeleteConfirm(data);
  }
  userDeleteConfirm(data: any): void {
    this.confirmationService.confirm({
      message: 'Are you sure that you want to delete ' + data.name + '?',
      header: 'Confirmation',
      icon: 'pi pi-exclamation-triangle',
      accept: () => {
        this.deleteRecord(data.budgetTermId);
      },
      key: 'confirmeKey',
    });
  }
  deleteRecord(budgetTermId: any) {
    if (budgetTermId) {
      this.inventorySrv.deleteBudgetTerm(budgetTermId).subscribe((res: any) => {
        if (res.code == 200) {
          this.messageService.add({
            key: 'successKey',
            severity: 'success',
            summary: 'Record deleted successfully',
            detail: res.text,
          });
          this.loadBudegetTermList();
        } else {
          this.messageService.add({
            key: 'successKey',
            severity: 'warn',
            summary: 'Delete failed',
            detail: res.text,
          });
        }
      });
    }
  }

  loadBudegetTermData(event: any) {
    if (event.code == 200) {
      this.messageService.add({
        key: 'successKey',
        severity: 'success',
        summary: 'success',
        detail: event.text,
      });
      this.loadBudegetTermList();
      this.isOpen = false;
    }
    if (event == 'true') {
      this.loadBudegetTermList();
      this.isOpen = false;
    }
  }
}
<div class="card" style="height: 90vh">
  <p-panel header="Inventory Budget Terms">
    <ng-template pTemplate="icons">
      <button pButton (click)="onAdd()" label="Add" icon="pi pi-plus"></button>
    </ng-template>
    <div class="card">
      <p-table
        [value]="budegetTermList"
        [paginator]="true"
        dataKey="budgetTermId"
        [rows]="rowCount"
        [tableStyle]="{ 'min-width': '60rem', width: '100%' }"
        styleClass="p-datatable-responsive-demo p-datatable-gridlines"
        [rowHover]="true"
        [scrollable]="true"
        scrollDirection="both"
        scrollHeight="540px"
        [showCurrentPageReport]="true"
        currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries"
        [rowsPerPageOptions]="[25, 50, 100]"
      >
        <ng-template pTemplate="header">
          <tr>
            <th style="width: 8rem"></th>
            <th pSortableColumn="name">
              <div class="grid-header-column">
                <div class="grid-header-label">
                  <p class="grid-label-paragraph">Term Name</p>
                </div>
                <div class="grid-sort-icon">
                  <p-sortIcon field="name"></p-sortIcon>
                </div>
              </div>
            </th>
            <th pSortableColumn="dateFrom">
              <div class="grid-header-column">
                <div class="grid-header-label">
                  <p class="grid-label-paragraph">Term Start Date</p>
                </div>
                <div class="grid-sort-icon">
                  <p-sortIcon field="dateFrom"></p-sortIcon>
                </div>
              </div>
            </th>
            <th pSortableColumn="dateTo">
              <div class="grid-header-column">
                <div class="grid-header-label">
                  <p class="grid-label-paragraph">Term End Date</p>
                </div>
                <div class="grid-sort-icon">
                  <p-sortIcon field="dateTo"></p-sortIcon>
                </div>
              </div>
            </th>
          </tr>
        </ng-template>
        <ng-template pTemplate="body" let-bdgterm>
          <tr>
            <td style="width: 8rem">
              <button
                pButton
                pRipple
                icon="pi pi-pencil"
                type="button"
                (click)="openEditItem(bdgterm.budgetTermId)"
                class="p-button-text"
              ></button>
              <button
                pButton
                pRipple
                icon="pi pi-times"
                type="button"
                (click)="onDelete(bdgterm)"
                class="p-button-text pull-right"
                style="font-size: 5rem; color: red"
              ></button>
            </td>
            <td>{{ bdgterm.name }}</td>
            <td>{{ bdgterm.dateFrom | date : "dd MMM yyyy" }}</td>
            <td>{{ bdgterm.dateTo | date : "dd MMM yyyy" }}</td>
          </tr>
        </ng-template>
        <ng-template pTemplate="emptymessage" let-columns>
          <tr>
            <td [attr.colspan]="4">No records found</td>
          </tr>
        </ng-template>
      </p-table>
    </div>
  </p-panel>
  <div *ngIf="isOpen">
    <app-add-edit-budget-terms
      [budegetTermData]="budegetTermData"
      (parentFun)="loadBudegetTermData($event)"
    ></app-add-edit-budget-terms>
  </div>
</div>
<p-toast key="successKey" position="top-right"></p-toast>
<p-confirmDialog
  key="confirmeKey"
  header="Confirmation"
  icon="pi pi-exclamation-triangle"
></p-confirmDialog>
