import { Component, forwardRef, OnDestroy, Input, ChangeDetectorRef } from '@angular/core';
import { Subscription } from 'rxjs';
import { UntypedFormGroup, UntypedFormBuilder, Validators, ValidatorFn, AbstractControl } from '@angular/forms';
import { ControlValueAccessor, NG_VALUE_ACCESSOR, UntypedFormControl, NG_VALIDATORS } from '@angular/forms';
import { Enums } from 'src/app/model/enums.model';
import { VaildationError } from 'src/app/model/vaildationerror.model';
import { BuildingService } from '../../services/bl.service';
import { BuildingFilterInput } from '../../model/DTO/blFilterInput.model';
import { SiteService } from '../../../../services/site.service';
import { SiteFilterInput } from '../../../site/modal/siteFilterInput.model';
import { AuthService } from '../../../../services/auth.service';
import { UtilConstant } from 'src/common/UtilConstant';
import { EmployeeService } from 'src/app/ui/employee/services/employee.service';
import { EmpFilterInput } from 'src/app/ui/employee/model/em-filter-input-dto';

@Component({
  selector: 'app-add-bl-form',
  templateUrl: './add-bl-form.component.html',
  styleUrls: ['./add-bl-form.component.scss'],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => AddBlFormComponent),
      multi: true
    },
    {
      provide: NG_VALIDATORS,
      useExisting: forwardRef(() => AddBlFormComponent),
      multi: true
    }
  ]
})
export class AddBlFormComponent implements ControlValueAccessor, OnDestroy {

  blFormPanel: UntypedFormGroup;
  subscriptions: Subscription[] = [];
  newRecord: boolean = true;
  enumList: Enums[] = [];
  enumComp: BuildingFilterInput[] = [];
  bl_img: any = "assets/images/blank-image.png";
  selectedFile!: any;
  isPhotoUploaded: boolean = false;
  @Input() isNew!: boolean;
  @Input() isView!: boolean;
  enumSites: SiteFilterInput[] = [];
  blContact:boolean =false;
  enumBlContactName: any[] =[];
  displayImage: boolean = false;
  timeZoneData: any[] = [];
  imageerrorMsg:string ='';
  NotImage:boolean = false;
  limitSite: number = 0;
  offsetSite: number = 0;
  limitEm: number = 0;
  offsetEm: number = 0;
  filterCriteria: any = {
    fieldName: null, value: null, matchMode: "contains", limit: 0, offset: 0
  };
  scrollLimit:number = UtilConstant.SCROLL_LIMIT;
  prevSite:any = {};
  selectedEm:any = {};
  constructor(
    private formBuilder: UntypedFormBuilder,
    private blSrv: BuildingService,
    private siteSrv: SiteService,
    private authSrv: AuthService,
    private cdr: ChangeDetectorRef,
    private emService: EmployeeService
  ) {
    this.blFormPanel = this.formBuilder.group({
      blId: [''],
      blCode: ['', [Validators.required ]],
      blName: [''],
      siteId: ['', [Validators.required]],
      blInfo: [''],
      longitude: [''],
      latitude: [''],
      blContactName: [''],
      blContactPhone: [''],
      blPhoto: [''],
      timeZoneId:[[Validators.required]],
    });

    this.subscriptions.push(
      // any time the inner form changes update the parent of any change
      this.blFormPanel.valueChanges.subscribe(value => {
        this.onChange(value);
        this.onTouched();
      })
    );
  }

  ngOnInit(): void {
    // this.loadCompany();
    //this.loadSites();
   /// this.scrollToEndEm();
    this.loadTimeZones();
  }

  preview() {
    this.displayImage = true;
  }

  loadSites() {
    this.siteSrv.getALLSites().subscribe((res: any[]) => {
      this.enumSites = res;
      this.enumSites = res.map((i: any) => { i.siteName = i.siteNameString; return i; });
      this.enumSites.unshift(new SiteFilterInput('', 'Make a selection',0));
    });
  }


  loadEmployeeContact():void{
    this.blContact = true;
    this.emService.getAllEmployeeList().subscribe((res: any[]) => {
      this.blContact = false;
      this.enumBlContactName = res;
      this.enumBlContactName.unshift(new EmpFilterInput('', 'Make a selection'));
    },error=>{
      this.blContact = false;
    });
  }

  public isValid() {
    return this.getValidationErrors().length === 0;
  }

  public getValidationErrors() {
    const me = this;
    const validationErros: VaildationError[] = [];

    return validationErros;
  }
  /// End Of Form Validation Related Code
  get value(): any {
    const blDetails: any = {
      blId: this.blFormPanel.controls.blId.value,
      blCode: this.blFormPanel.controls.blCode.value,
      siteId: this.blFormPanel.controls.siteId.value,
      blName: this.blFormPanel.controls.blName.value,
      blInfo: this.blFormPanel.controls.blInfo.value,
      longitude: this.blFormPanel.controls.longitude.value,
      latitude: this.blFormPanel.controls.latitude.value,
      blContactName: this.blFormPanel.controls.blContactName.value,
      blContactPhone: this.blFormPanel.controls.blContactPhone.value,
      blPhoto: 2,
      timeZoneId: this.blFormPanel.controls.timeZoneId.value
    };
    return blDetails;
  }

  set value(value: any) {
    if(value.siteId) {
      const siteData:any = {
        siteId:value.siteId,
        siteName:value.site.siteName,
      }
      this.prevSite = siteData;
      this.addPrevSite(this.prevSite);
    }
    if(value.blContactName) {
      this.selectedEm = value.blContactName;
      this.updateEmList(value.blContactName);
    }
    setTimeout(() => {
      this.blFormPanel.patchValue({
        blId: value.blId,
        blCode: value.blCode,
        siteId: value.siteId,
        blName: value.blName,
        blInfo: value.blInfo,
        longitude: value.longitude,
        latitude: value.latitude,
        blContactName: value.blContactName,
        blContactPhone: value.blContactPhone,
        timeZoneId: value.timeZoneId
      });

    });
    this.onChange(value);
    this.onTouched();
  }

  writeValue(value: any) {

    if (value) {
      this.value = value;
    }
    if (value === null) {
      this.blFormPanel.reset();
    }
  }


  registerOnChange(fn: any) {
    this.onChange = fn;
  }
  registerOnTouched(fn: any) {
    this.onTouched = fn;
  }
  onChange: any = () => { };
  onTouched: any = () => { };

  // communicate the inner form validation to the parent form
  validate(_: UntypedFormControl) {
    return this.blFormPanel.valid ? null : { blFormPanel: { valid: false } };
  }

  ngOnDestroy() {
    this.subscriptions.forEach(s => s.unsubscribe());
  }

  fileChangeEvent(event: any) {
    let me = this;
    let fileReader = new FileReader();
    var file = event.target.files[0];
    this.selectedFile = file;
    let filename = file.name;
    var extFile = filename.substr(filename.lastIndexOf(".") + 1, filename.length).toLowerCase();
    if (extFile=="jpg" || extFile=="jpeg" || extFile=="png"){
      var imgSize = event.target.files[0].size;
        if(imgSize > 1000000) {
          this.NotImage = true;
          this.imageerrorMsg = 'Image Size should not be greater than 1 MB';
        }else{
          this.NotImage = false;
          this.imageerrorMsg = '';
          me.isPhotoUploaded = true;
          fileReader.readAsDataURL(file);
          console.log(fileReader.result);
          fileReader.onload = function () {
          me.bl_img = fileReader.result;
        }} 
    }else{
        this.NotImage = true;
        this.imageerrorMsg = 'Only jpg/jpeg and png files are allowed!';
    }
  }

  loadTimeZones(){
    this.blSrv.getAllTimeZones().subscribe((res:any) => {
      this.timeZoneData = res.map((t:any) => {t.displayName = t.timeZoneId + " - " + t.description; return t})

    })
  }

  scrollToEndSite() {
    this.offsetSite = this.limitSite;
    this.limitSite += this.scrollLimit;
    this.filterCriteria.limit = this.limitSite;
    this.filterCriteria.offset = this.offsetSite;
    this.siteSrv.getAllSiteByScroll(this.filterCriteria).subscribe((res:any) => {
     this.enumSites = res;
     this.addPrevSite(this.prevSite);
    })
   }

   searchSite(event:any) {
    this.filterCriteria = {};
    this.filterCriteria = { fieldName: "siteName", value: event.term, matchMode: "contains" };
    this.scrollToEndSite();
   }

   addPrevSite(prevSite:any) {
    if(prevSite) {
      if(!this.isNew && prevSite.siteId){
        this.enumSites = this.enumSites.filter(t => t.siteId !== prevSite.siteId);
        this.enumSites = this.enumSites.filter(t => t.siteId !== null);
        this.enumSites.unshift(prevSite);

     }
     this.enumSites.unshift(new SiteFilterInput(null,'Make a selection',0));
    }
   }

   scrollToEndEm() {
    this.offsetEm = this.limitEm;
    this.limitEm += this.scrollLimit;
    this.filterCriteria.limit = this.limitEm;
    this.filterCriteria.offset = this.offsetEm;
    this.emService.getALLmployeeByScroll(this.filterCriteria).subscribe((res:any) => {
      this.enumBlContactName = res;
      this.enumBlContactName.unshift({emId:null, firstName:'Make a selection',emCode:null});
      this.updateEmList(this.selectedEm);
    })
  }

  searchEm(event: any) {
    this.filterCriteria = {};
    this.filterCriteria = { fieldName: "firstName", value: event.term, matchMode: "contains" };
    this.scrollToEndEm();
  }

  updateEmList(emData:any) {
   if(emData) {
    this.enumBlContactName = this.enumBlContactName.filter(e => e.firstName != emData);
    this.enumBlContactName = this.enumBlContactName.filter(e => e.emId != null);
    this.enumBlContactName.unshift(emData);
   }
    this.enumBlContactName.unshift({emId:null, firstName:'Make a selection',emCode:null});
  }

  onOpenSite() {
    this.limitSite = 0;
    this.offsetSite = 0;
    this.filterCriteria = {
      fieldName: null, value: null, matchMode: "contains", limit: 0, offset: 0
    };
    this.scrollToEndSite();
  }

  onOpenEm() {
    this.limitEm = 0;
    this.offsetEm = 0;
    this.filterCriteria = {
      fieldName: null, value: null, matchMode: "contains", limit: 0, offset: 0
    };
    this.scrollToEndEm();
  }

}
<form [formGroup]="blFormPanel">
  <table width="100%">
    <tr>
      <td style="width: 50%">
        <div class="input-label-custom ">
          <label class='form-field-lable control-label'>Building Code</label>
          <input type="text" class="form-control" name="blCode" uppercase formControlName="blCode" maxlength="32" [readonly]="isView" appTrimInput>
        </div>
      </td>
      <td style="width: 50%">
        <div class="input-label-custom ">
          <label class='form-field-lable'>Building Name</label>
          <input type="text" class="form-control" [readonly]="isView" name="blName" formControlName="blName" maxlength="64" appTrimInput>
        </div>
      </td>
    </tr>
    <tr *ngIf="(blFormPanel.controls['blId']?.errors && (blFormPanel.controls['blId']?.dirty || blFormPanel.controls['blId']?.touched))">
            <td style="width: 50%">
                    <label *ngIf="blFormPanel.controls['blId']?.errors && !blFormPanel.controls['blId']?.errors.required"
                            class='form-field-lable' style="color:red;">Building Id and Site Id combination  already
                            exists.</label>
            </td>
            <td></td>
    </tr>
    <tr>
      <td style="width: 50%">
        <div class="input-label-custom ">
          <label class='form-field-lable control-label'>Site </label>
          <ng-select appendTo="body" [clearable]="false" [readonly]="!isNew" [items]="enumSites" formControlName="siteId"
          [readonly]="isView"  [searchable]="true" bindLabel="siteName" bindValue="siteId" placeholder="Make a selection"
          (open)="onOpenSite()"   class="ng-select-dropdown" (scrollToEnd)="scrollToEndSite()" (search)="searchSite($event)">
          </ng-select>
        </div>
      </td>
      <td style="width: 50%">
        <div class="input-label-custom ">
          <label class='form-field-lable'>Building Contact Name</label>
          <ng-select appendTo="body" [clearable]="false" [loading]="blContact" [items]="enumBlContactName" (open)="onOpenEm()"
            formControlName="blContactName" [readonly]="isView" [searchable]="true" bindLabel="firstName" bindValue="firstName"
            placeholder="Make a selection" class="ng-select-dropdown" (scrollToEnd)="scrollToEndEm()" (search)="searchEm($event)">
          </ng-select>
        </div>
      </td>
    </tr>
    <tr>
      <td style="width: 50%">
        <div class="input-label-custom ">
          <label class='form-field-lable'>Contact Number</label>
          <input type="text" class="form-control"  [readonly]="isView" name="blContactPhone" NumbersOnly formControlName="blContactPhone" maxlength="64" appTrimInput>
        </div>
      </td>
      <td style="width: 50%">
        <div class="input-label-custom ">
          <label class='form-field-lable'>Latitude</label>
          <input type="number" class="form-control blue-border" [readonly]="isView" name="latitude" formControlName="latitude" NumbersOnly onKeyPress="if(this.value.length==8) return false;" appTrimInput>
        </div>
      </td>
    </tr>
    <tr>
      <td style="width: 50%">
        <div class="input-label-custom ">
          <label class='form-field-lable'>Longitude</label>
          <input type="number" class="form-control blue-border" [readonly]="isView" name="longitude" formControlName="longitude" NumbersOnly onKeyPress="if(this.value.length==8) return false;" appTrimInput>
        </div>
      </td>
      <td style="width: 50%" rowspan="2" [hidden]="isNew">
        <table style="width: 100%">
          <tr>
            <td style="align-content:flex-start">
              <div class="container">
                <img [src]="bl_img" alt="" class="image" style="width: 100px; height: 100px;">
                <div class="middle">
                  <div class="text">
                    <button icon="pi pi-eye" pButton pRipple type="button" (click)="preview()" class="p-button-text">
                    </button>
                  </div>
                </div>
              </div>
            </td>
            <td style="width: 50%">
              <div class="input-label-custom ">
                <button pButton label="Select Photo" style="margin-left:4px" class="p-button" [disabled]="isView" type="button" icon="pi pi-image"
                        iconPos="left" (click)="_fileInput.click()"></button>
                <div>
                    <input #_fileInput type="file" accept="image/*" (change)="fileChangeEvent($event)" hidden />
                    <p *ngIf="NotImage" class='form-field-lable' style="color: red;font-size: 10px;">{{imageerrorMsg}}</p>
                </div>

              </div>
            </td>
          </tr>
        </table>
      </td>
    </tr>
    <tr>
      <td></td>
      <td></td>
    </tr>
    <tr>
      <td style="width: 50%">
        <div class="input-label-custom ">
          <label class='form-field-lable control-label'>Time Zone</label>
          <ng-select appendTo="body" [clearable]="false" [readonly]="!isNew" [items]="timeZoneData" formControlName="timeZoneId"
          [readonly]="isView"  [searchable]="true" bindLabel="displayName" bindValue="timeZoneId" placeholder="Make a selection"
                     class="ng-select-dropdown">
          </ng-select>
        </div>
      </td>
    </tr>
    <tr>
      <td style="width: 50%" colspan="2">
        <div class="input-label-custom ">
          <label class='form-field-lable'>Building Info</label>
          <textarea class="form-control note-border" [readonly]="isView" name="blInfo" formControlName="blInfo" rows="2" nz-input maxlength="128" appTrimInput></textarea>
        </div>
      </td>
    </tr>
  </table>
  <p-dialog [(visible)]="displayImage" [style]="{width: '30vw'}">
    <img [src]="bl_img" [alt]="" width="90%" height="90%" />
  </p-dialog>
</form>
