    styles: [".p-card .p-card-title {\n  font-size: 1.5rem;\n  font-weight: 700;\n  margin-bottom: 0.5rem;\n}\n\n  .ng-select.ng-select-single .ng-select-container .ng-value-container .ng-value {\n  overflow: hidden !important;\n  text-overflow: ellipsis !important;\n  white-space: nowrap !important;\n  max-width: 350px !important;\n}\n\n  .p-card .p-card-body {\n  padding: 10px;\n}\n\n  .mat-typography h2 {\n  margin: 0px;\n  font-size: 16px !important;\n  padding-left: 5px;\n}\n\n  .p-card .p-card-content {\n  padding: 0px;\n}\n\n.margin-class[_ngcontent-%COMP%] {\n  margin: 10px;\n}\n\n  .mat-dialog-content {\n  overflow: hidden !important;\n}\n\n.field-checkbox[_ngcontent-%COMP%] {\n  display: inline-block;\n  max-width: 200px;\n}\n\n.field-checkbox[_ngcontent-%COMP%]    + .field-checkbox[_ngcontent-%COMP%] {\n  margin-top: 10px;\n}\n\n.ellipsis[_ngcontent-%COMP%] {\n  white-space: nowrap;\n  overflow: hidden;\n  text-overflow: ellipsis;\n}\n\n  .p-datatable-table {\n  table-layout: fixed !important;\n}\n\n  .cdk-overlay-container {\n  z-index: 1400;\n}\n\n.location-container[_ngcontent-%COMP%] {\n  display: flex;\n}\n\n.input-container[_ngcontent-%COMP%] {\n  width: 92%;\n}\n\n.icon-container[_ngcontent-%COMP%] {\n  display: flex;\n  align-self: center;\n  margin-top: 16px;\n}\n\n.blue-icon[_ngcontent-%COMP%] {\n  color: #2196F3;\n  cursor: pointer;\n}\n\n.no-file-found[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: center;\n  align-items: center;\n  height: 500px;\n  padding-top: 5px;\n}\n\n[_nghost-%COMP%]     .p-dialog-header {\n  background-color: #879cc9;\n  padding-left: 10px;\n  font-weight: normal;\n  margin-bottom: 0px;\n  height: 10px !important;\n}\n\n.msg-label[_ngcontent-%COMP%] {\n  font-size: 15px;\n  margin-bottom: 0px;\n  font-weight: 500;\n  font-family: sans-serif;\n  color: black;\n}\n\n.stop-event[_ngcontent-%COMP%] {\n  pointer-events: none;\n}\n\n.priority-container[_ngcontent-%COMP%] {\n  display: grid;\n  overflow-y: scroll;