import { Component, OnInit } from '@angular/core';
import { CenterUsage } from '../model/CenterUsage';
import { UtilConstant } from 'src/common/UtilConstant';
import { InventoryService } from '../../service/inventory.service';
import { ConfirmationService, MessageService } from 'primeng/api';
import { BuildingService } from 'src/app/ui/background-loc/services/bl.service';

@Component({
  selector: 'app-center-usage-list',
  templateUrl: './center-usage-list.component.html',
  styleUrls: ['./center-usage-list.component.scss'],
  providers: [MessageService],
})
export class CenterUsageListComponent implements OnInit {
  centerUsageList: CenterUsage[] = [];
  budgetTermList: any[] = [];
  blIdList: any[] = [];
  flIdList: any[] = [];
  itemList: any[] = [];
  centerUsageData: any;
  rowCount: number = UtilConstant.ROW_COUNT;
  isOpen: boolean = false;
  constructor(
    private inventorySrv: InventoryService,
    private confirmationService: ConfirmationService,
    private messageService: MessageService,
    private blSrv: BuildingService
  ) {}
  ngOnInit(): void {
    this.loadCenterUsageList();
    this.loadEnumsList();
  }

  loadCenterUsageList() {
    this.inventorySrv.getAllCenterUsage().subscribe((res: any) => {
      this.centerUsageList = res && !res.code ? res : [];
    });
  }
  loadEnumsList() {
    this.inventorySrv.getAllBudegetTerm().subscribe((res: any) => {
      this.budgetTermList = res && !res.code ? res : [];
    });
    this.inventorySrv.getAllItems().subscribe((res: any) => {
      this.itemList = res && !res.code ? res : [];
    });
    this.blSrv.getALLBuilding().subscribe((res: any) => {
      this.blIdList = res && !res.code ? res : [];
    });
    this.blSrv.getALLFloor().subscribe((res: any) => {
      this.flIdList = res && !res.code ? res : [];
    });
  }

  openEditItem(id: number) {
    this.centerUsageData = {
      centerUsageId: id,
      isNew: false,
    };
    this.isOpen = true;
  }

  onAdd() {
    this.centerUsageData = {
      centerUsageId: 0,
      isNew: true,
    };
    this.isOpen = true;
  }
  onDelete(data: CenterUsage) {
    this.userDeleteConfirm(data);
  }
  userDeleteConfirm(data: any): void {
    this.confirmationService.confirm({
      message: 'Are you sure that you want to delete ?',
      header: 'Confirmation',
      icon: 'pi pi-exclamation-triangle',
      accept: () => {
        this.deleteRecord(data.centerUsageId);
      },
      key: 'confirmeKey',
    });
  }
  deleteRecord(centerUsageId: any) {
    if (centerUsageId) {
      this.inventorySrv
        .deleteCenterUsage(centerUsageId)
        .subscribe((res: any) => {
          if (res.code == 200) {
            this.messageService.add({
              key: 'successKey',
              severity: 'success',
              summary: 'Record deleted successfully',
              detail: res.text,
            });
            this.loadCenterUsageList();
          } else {
            this.messageService.add({
              key: 'successKey',
              severity: 'warn',
              summary: 'Delete failed',
              detail: res.text,
            });
          }
        });
    }
  }
  getNameById(id: any, type: string) {
    let sourceList: any[] = [];
    let name: string = '';
    switch (type) {
      case 'BUDGET':
        sourceList = this.budgetTermList;
        name = sourceList.find((v) => v.budgetTermId === id)?.name || '';
        break;
      case 'BUILDING':
        sourceList = this.blIdList;
        name = sourceList.find((v) => v.blId === id)?.blNameString || '';
        break;
      case 'FLOOR':
        sourceList = this.flIdList;
        name = sourceList.find((v) => v.flId === id)?.flNameString || '';
        break;
      case 'ITEM':
        sourceList = this.itemList;
        name = sourceList.find((v) => v.itemId === id)?.name || '';
        break;
      default:
        break;
    }
    return name;
  }

  loadCenterUsageData(event: any) {
    if (event.code == 200) {
      this.messageService.add({
        key: 'successKey',
        severity: 'success',
        summary: 'success',
        detail: event.text,
      });
      this.loadCenterUsageList();
      this.isOpen = false;
    }
    if (event == 'true') {
      this.loadCenterUsageList();
      this.isOpen = false;
    }
  }
}
<div class="card" style="height: 90vh">
  <p-panel header="Inventory Center Usage">
    <ng-template pTemplate="icons">
      <button pButton (click)="onAdd()" label="Add" icon="pi pi-plus"></button>
    </ng-template>
    <p-table
      [value]="centerUsageList"
      [paginator]="true"
      dataKey="centerUsageId"
      [rows]="rowCount"
      [tableStyle]="{ 'min-width': '68rem', width: '100%' }"
      styleClass="p-datatable-responsive-demo p-datatable-gridlines"
      [rowHover]="true"
      [scrollable]="true"
      scrollDirection="both"
      scrollHeight="540px"
      [showCurrentPageReport]="true"
      currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries"
      [rowsPerPageOptions]="[25, 50, 100]"
    >
      <ng-template pTemplate="header">
        <tr>
          <th style="width: 8rem"></th>
          <th pSortableColumn="budgetTermId">
            <div class="grid-header-column">
              <div class="grid-header-label">
                <p class="grid-label-paragraph">Budget Term Id</p>
              </div>
              <div class="grid-sort-icon">
                <p-sortIcon field="budgetTermId"></p-sortIcon>
              </div>
            </div>
          </th>
          <th pSortableColumn="blId">
            <div class="grid-header-column">
              <div class="grid-header-label">
                <p class="grid-label-paragraph">Building Id</p>
              </div>
              <div class="grid-sort-icon">
                <p-sortIcon field="blId"></p-sortIcon>
              </div>
            </div>
          </th>
          <th pSortableColumn="flId">
            <div class="grid-header-column">
              <div class="grid-header-label">
                <p class="grid-label-paragraph">Floor Id</p>
              </div>
              <div class="grid-sort-icon">
                <p-sortIcon field="flId"></p-sortIcon>
              </div>
            </div>
          </th>
          <th pSortableColumn="itemId">
            <div class="grid-header-column">
              <div class="grid-header-label">
                <p class="grid-label-paragraph">Item Id</p>
              </div>
              <div class="grid-sort-icon">
                <p-sortIcon field="itemId"></p-sortIcon>
              </div>
            </div>
          </th>
          <th pSortableColumn="quantity">
            <div class="grid-header-column">
              <div class="grid-header-label">
                <p class="grid-label-paragraph">Quantity</p>
              </div>
              <div class="grid-sort-icon">
                <p-sortIcon field="quantity"></p-sortIcon>
              </div>
            </div>
          </th>
          <th pSortableColumn="rate">
            <div class="grid-header-column">
              <div class="grid-header-label">
                <p class="grid-label-paragraph">Cost Per Unit</p>
              </div>
              <div class="grid-sort-icon">
                <p-sortIcon field="rate"></p-sortIcon>
              </div>
            </div>
          </th>
          <th pSortableColumn="cost">
            <div class="grid-header-column">
              <div class="grid-header-label">
                <p class="grid-label-paragraph">Cost</p>
              </div>
              <div class="grid-sort-icon">
                <p-sortIcon field="cost"></p-sortIcon>
              </div>
            </div>
          </th>
        </tr>
      </ng-template>
      <ng-template pTemplate="body" let-centerUsage>
        <tr>
          <td style="width: 8rem">
            <button
              pButton
              pRipple
              icon="pi pi-pencil"
              type="button"
              (click)="openEditItem(centerUsage.centerUsageId)"
              class="p-button-text"
            ></button>
            <button
              pButton
              pRipple
              icon="pi pi-times"
              type="button"
              (click)="onDelete(centerUsage)"
              class="p-button-text pull-right"
              style="font-size: 5rem; color: red"
            ></button>
          </td>
          <td>{{ getNameById(centerUsage.budgetTermId, "BUDGET") }}</td>
          <td>{{ getNameById(centerUsage.blId, "BUILDING") }}</td>
          <td>{{ getNameById(centerUsage.flId, "FLOOR") }}</td>
          <td>{{ getNameById(centerUsage.itemId, "ITEM") }}</td>
          <td>{{ centerUsage.quantity }}</td>
          <td>{{ centerUsage.rate }}</td>
          <td>{{ centerUsage.cost }}</td>
        </tr>
      </ng-template>
      <ng-template pTemplate="emptymessage" let-columns>
        <tr>
          <td [attr.colspan]="8">No records found</td>
        </tr>
      </ng-template>
    </p-table>
  </p-panel>
  <div *ngIf="isOpen">
    <app-add-edit-center-usage
      [centerUsageData]="centerUsageData"
      (parentFun)="loadCenterUsageData($event)"
    ></app-add-edit-center-usage>
  </div>
</div>
<p-toast key="successKey" position="top-right"></p-toast>
<p-confirmDialog
  key="confirmeKey"
  header="Confirmation"
  icon="pi pi-exclamation-triangle"
></p-confirmDialog>
