import { Component, OnInit } from '@angular/core';
import { InventoryService } from '../service/inventory.service';
import { Item } from './model/Item';
import { ConfirmationService, MessageService } from 'primeng/api';
import { UtilConstant } from 'src/common/UtilConstant';

@Component({
  selector: 'app-item-list',
  templateUrl: './item-list.component.html',
  styleUrls: ['./item-list.component.scss'],
  providers: [MessageService],
})
export class ItemListComponent implements OnInit {
  itemsList: Item[] = [];
  itemData: any;
  rowCount: number = UtilConstant.ROW_COUNT;
  isOpen: boolean = false;
  constructor(
    private inventorySrv: InventoryService,
    private confirmationService: ConfirmationService,
    private messageService: MessageService
  ) {}
  ngOnInit(): void {
    this.loadItemList();
  }

  loadItemList() {
    this.inventorySrv.getAllItems().subscribe((res: any) => {
      this.itemsList = res && !res.code ? res : [];
    });
  }

  openEditItem(id: number) {
    this.itemData = {
      itemId: id,
      isNew: false,
    };
    this.isOpen = true;
  }

  onAdd() {
    this.itemData = {
      itemId: 0,
      isNew: true,
    };
    this.isOpen = true;
  }
  onDelete(data: Item) {
    this.userDeleteConfirm(data);
  }
  userDeleteConfirm(data: any): void {
    this.confirmationService.confirm({
      message: 'Are you sure that you want to delete ' + data.name + '?',
      header: 'Confirmation',
      icon: 'pi pi-exclamation-triangle',
      accept: () => {
        this.deleteRecord(data.itemId);
      },
      key: 'confirmeKey',
    });
  }
  deleteRecord(itemId: any) {
    if (itemId) {
      this.inventorySrv.deleteItem(itemId).subscribe((res: any) => {
        if (res.code == 200) {
          this.messageService.add({
            key: 'successKey',
            severity: 'success',
            summary: 'Record deleted successfully',
            detail: res.text,
          });
          this.loadItemList();
        } else {
          this.messageService.add({
            key: 'successKey',
            severity: 'warn',
            summary: 'Delete failed',
            detail: res.text,
          });
        }
      });
    }
  }
  loadItemData(event: any) {
    if (event.code == 200) {
      this.messageService.add({
        key: 'successKey',
        severity: 'success',
        summary: 'success',
        detail: event.text,
      });
      this.loadItemList();
      this.isOpen = false;
    }

    if (event == 'true') {
      this.loadItemList();
      this.isOpen = false;
    }
  }
}
<div class="card" style="height: 90vh">
  <p-panel header="Inventory Items">
    <ng-template pTemplate="icons">
      <button pButton (click)="onAdd()" label="Add" icon="pi pi-plus"></button>
    </ng-template>
    <div class="card">
      <p-table
        [value]="itemsList"
        [paginator]="true"
        dataKey="itemId"
        [rows]="rowCount"
        [tableStyle]="{ 'min-width': '60rem', width: '100%' }"
        styleClass="p-datatable-responsive-demo p-datatable-gridlines"
        [rowHover]="true"
        [scrollable]="true"
        scrollDirection="both"
        scrollHeight="540px"
        [showCurrentPageReport]="true"
        currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries"
        [rowsPerPageOptions]="[25, 50, 100]"
      >
        <ng-template pTemplate="header">
          <tr>
            <th style="width: 8rem"></th>
            <th pSortableColumn="name">
              <div class="grid-header-column">
                <div class="grid-header-label">
                  <p class="grid-label-paragraph">Item Name</p>
                </div>
                <div class="grid-sort-icon">
                  <p-sortIcon field="name"></p-sortIcon>
                </div>
              </div>
            </th>
            <th pSortableColumn="stock">
              <div class="grid-header-column">
                <div class="grid-header-label">
                  <p class="grid-label-paragraph">Stock</p>
                </div>
                <div class="grid-sort-icon">
                  <p-sortIcon field="stock"></p-sortIcon>
                </div>
              </div>
            </th>
            <th pSortableColumn="unit">
              <div class="grid-header-column">
                <div class="grid-header-label">
                  <p class="grid-label-paragraph">Unit</p>
                </div>
                <div class="grid-sort-icon">
                  <p-sortIcon field="unit"></p-sortIcon>
                </div>
              </div>
            </th>
            <th pSortableColumn="rate">
              <div class="grid-header-column">
                <div class="grid-header-label">
                  <p class="grid-label-paragraph">Cost Per Unit</p>
                </div>
                <div class="grid-sort-icon">
                  <p-sortIcon field="rate"></p-sortIcon>
                </div>
              </div>
            </th>
            <th pSortableColumn="minStockReq">
              <div class="grid-header-column">
                <div class="grid-header-label">
                  <p class="grid-label-paragraph">Minimum Stock Required</p>
                </div>
                <div class="grid-sort-icon">
                  <p-sortIcon field="minStockReq"></p-sortIcon>
                </div>
              </div>
            </th>
          </tr>
        </ng-template>
        <ng-template pTemplate="body" let-item>
          <tr>
            <td style="width: 8rem">
              <button
                pButton
                pRipple
                icon="pi pi-pencil"
                type="button"
                (click)="openEditItem(item.itemId)"
                class="p-button-text"
              ></button>
              <button
                pButton
                pRipple
                icon="pi pi-times"
                type="button"
                (click)="onDelete(item)"
                class="p-button-text pull-right"
                style="font-size: 5rem; color: red"
              ></button>
            </td>
            <td>{{ item.name }}</td>
            <td>{{ item.stock }}</td>
            <td>{{ item.unit }}</td>
            <td>{{ item.rate }}</td>
            <td>{{ item.minStockReq }}</td>
          </tr>
        </ng-template>
        <ng-template pTemplate="emptymessage" let-columns>
          <tr>
            <td [attr.colspan]="6">No records found</td>
          </tr>
        </ng-template>
      </p-table>
    </div>
  </p-panel>
  <div *ngIf="isOpen">
    <app-add-edit-item
      [itemData]="itemData"
      (parentFun)="loadItemData($event)"
    ></app-add-edit-item>
  </div>
</div>
<p-toast key="successKey" position="top-right"></p-toast>
<p-confirmDialog
  key="confirmeKey"
  header="Confirmation"
  icon="pi pi-exclamation-triangle"
></p-confirmDialog>
