    this.buildSideNav();
    this.getEmployeeDetails();
    this.pageTitle = this.cookieSrv.get("pageTitle");
  }
  ngOnInit() {
    this.isCollapsed = false;
    this.ss.getEmittedValue().subscribe(item => {
      this.emPhoto = "";
      this.isLogin = item;
      if (this.isLogin) {
        this.preinit();
      }
    });
    if (this.tokenService.getToken()) {
