      this.filterPanel.controls[fieldId].setValue(null);
    }
  }
  notify() {
    this.useTabletProtrait = BreakpointService.useTabletProtrait;
  }
  onSelectSiteCode(event) {
    setTimeout(() => {
      this.filterPanel.patchValue({
        blId: null,
        flId: null,
        rmId: null
      });
    }, 10);
    if (event.siteId != null) {
      this.selectedSite = event;
      this.selectedBl = {};
    } else {
      this.selectedSite = {};
    }
  }
  onSelectBlCode($event) {
    setTimeout(() => {
      this.filterPanel.patchValue({
        flId: null,
        rmId: null
      });
    }, 10);
    if ($event.blId != null) {
      this.selectedBl = $event;
      const siteData = {
        siteId: $event.siteId,
        siteNameString: $event.siteNameString
      };
      this.selectedSite = siteData;
      this.updateSiteList(siteData);
      this.selectedFl = {};
      setTimeout(() => {
        this.filterPanel.patchValue({
          siteId: $event.siteId
        });
      }, 10);
    } else {
      this.selectedBl = {};
      this.selectedFl = {};
    }
  }
  onSelectFlCode(event) {
    if (event.flId != null) {
      this.selectedFl = event;
      const siteData = {
        siteId: event.siteId,
        siteNameString: event.siteNameString
      };
      this.selectedSite = siteData;
      this.updateSiteList(siteData);
      const blData = {
        blId: event.blId,
        blNameString: event.blNameString,
        site: null
      };
      this.selectedBl = blData;
      this.updateBlList(blData);
      this.selectedRm = {};
      setTimeout(() => {
        this.filterPanel.patchValue({
          siteId: event.siteId,
          blId: event.blId,
          rmId: null
        });
      }, 10);
    } else {
      this.selectedFl = {};
      this.selectedRm = {};
    }
  }
