      let rmtranstypeEnumList = res.filter(t => t.tableName.toLocaleUpperCase() === 'rm_trans'.toLocaleUpperCase() && t.fieldName.toLocaleUpperCase() === 'type'.toLocaleUpperCase() && t.enumValue.toLocaleUpperCase() === 'Temporary'.toLocaleUpperCase());
      this.enumTemporaryValue = rmtranstypeEnumList[0].enumKey;
